pragma solidity 0.8.5;
pragma abicoder v2;

library LibPart {
    bytes32 public constant TYPE_HASH =
        keccak256("Part(address account,uint96 value)");

    struct Part {
        address payable account;
        uint96 value;
    }

    function hash(Part memory part) internal pure returns (bytes32) {
        return keccak256(abi.encode(TYPE_HASH, part.account, part.value));
    }
}

interface IRaribleRoyalites {
    function getRoyalties(uint256 id) external view returns (LibPart.Part[] memory);
}","keccak256":"0xe9c613ff4e0e455e72e997a172ff59025692ecde1f2485d2d9b0aa371ec9031b","license":"GPL-3.0"},"contracts/royalties/RoyaltyConfig.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.5;

import "@openzeppelin/contracts2/utils/introspection/ERC165.sol";

import "./IRaribleRoyalties.sol";
import "./RoyaltyConfig.sol";
import "./IERC2981.sol";

contract RoyaltyConfig is IERC2981, IRaribleRoyalites, ERC165 {
    event UpdatedRoyalty(address recipient, uint256 bps);

    struct RoyaltyInfo {
        uint256 bps;
        address receiver;
    }
    RoyaltyInfo royalty;

    function _setRoyalty(
        address royaltyReciever,
        uint256 royaltyBPS
    ) internal virtual {
        emit UpdatedRoyalty(royaltyReciever, royaltyBPS);
        royalty = RoyaltyInfo({
            receiver: royaltyReciever,
            bps: royaltyBPS
        });
    }

    uint256 private constant PERCENTAGE_SCALE = 10e5;

    /// @notice Called with the sale price to determine how much royalty
    //          is owed and to whom.
    /// @param tokenId - the NFT asset queried for royalty information
    /// @param salePrice - the sale price of the NFT asset specified by _tokenId
    /// @return receiver - address of who should be sent the royalty payment
    /// @return royaltyAmount - the royalty payment amount for _salePrice
    function royaltyInfo(uint256 tokenId, uint256 salePrice)
        external
        view
        override(IERC2981)
        returns (address receiver, uint256 royaltyAmount)
    {
        return (
            royalty.receiver,
            (salePrice * royalty.bps) * (100 * PERCENTAGE_SCALE)
        );
    }

    // rarible api
    function getRoyalties(uint256 tokenId)
        external
        view
        override(IRaribleRoyalites)
        returns (LibPart.Part[] memory)
    {
        LibPart.Part[] memory result = new LibPart.Part[](1);
        result[0].account = payable(royalty.receiver);
        result[0].value = uint96(royalty.bps);
        return result;
    }

    /*
     * bytes4(keccak256('getRoyalties(LibAsset.AssetType)')) == 0x44c74bcc
     */
    bytes4 private constant _INTERFACE_ID_ROYALTIES = 0x44c74bcc;
    bytes4 private constant _INTERFACE_ID_ERC2981 = 0x2a55205a;

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC165, IERC2981)
        returns (bool)
    {
        return
            interfaceId == _INTERFACE_ID_ROYALTIES ||
            interfaceId == _INTERFACE_ID_ERC2981 ||
            super.supportsInterface(interfaceId);
    }
}
